	//Disabled because of use NeedItUtils var dayWhenNeeded = whenNeeded.getLocalDate();
	//Disabled because of use NeedItUtils var compareNeedNow = dayWhenNeeded.compareTo(dayRightnow);
	//Disabled because of use NeedItUtils gs.addInfoMessage("compareNeedNow: "+compareNeedNow);

	// If the When needed date is today
	// Output an error message to the screen
	//Disabled because of use NeedItUtils if(compareNeedNow == 0){
		//Disabled because of use NeedItUtils gs.addErrorMessage("When needed date cannot be today. You might need to check Timezone. Your request has not been saved to the database.");
		//Disabled because of use NeedItUtils current.setAbortAction(true);
	//Disabled because of use NeedItUtils }

	// If the When needed date is before rightnow, do not write the record to the database
	// Output an error message to the screen
	//Disabled because of use NeedItUtils if(whenNeeded.before(rightnow)){
		//Disabled because of use NeedItUtils gs.addErrorMessage("When needed date cannot be in the past.  Your request has not been saved to the database.");
		//Disabled because of use NeedItUtils current.setAbortAction(true);
	//Disabled because of use NeedItUtils }


})(current, previous);
